  const root = useRef<HTMLDivElement | null>(null);

  useGSAP(() => {
    const svg = root.current?.querySelector("svg") as SVGSVGElement | null;
    if (!svg) return;

    const l = svg.querySelector("#l-eye-mover") as SVGGElement | null;
    const r = svg.querySelector("#r-eye-mover") as SVGGElement | null;
    if (!l || !r) return;

    const prefersReduced =
      window.matchMedia?.("(prefers-reduced-motion: reduce)").matches ?? false;
    const isLarge = window.matchMedia("(min-width: 1024px)").matches;
    if (prefersReduced || !isLarge) return;

    gsap.set([l, r], { transformBox: "fill-box", transformOrigin: "50% 50%" });

    const DX = 44; // px horizontal max
    const DY = 18; // px vertical max
    const MAX_VERG = 0.35; // max *normalized* horizontal separation between eyes
    const DEAD = 0.08; // dead-zone radius (normalized) to prevent micro jitter

    const clamp = (v: number, a: number, b: number) =>
      Math.max(a, Math.min(b, v));
    const normFromEye = (eye: SVGGElement, x: number, y: number) => {
      const rect = eye.getBoundingClientRect();
      const cx = rect.left + rect.width / 2;
      const cy = rect.top + rect.height / 2;
      const dx = (x - cx) / (rect.width * 1.2); // ~eye width based scaling
      const dy = (y - cy) / (rect.height * 1.2);

      // dead zone (small circle around center -> snap to 0)
      if (dx * dx + dy * dy < DEAD * DEAD) return { nx: 0, ny: 0 };

      return {
        nx: clamp(dx, -1, 1),
        ny: clamp(dy, -1, 1),
      };
    };

    function moveEyes(e: MouseEvent) {
      if (!l || !r) return;
      // independent normalized aims
      const L = normFromEye(l, e.clientX, e.clientY);
      const R = normFromEye(r, e.clientX, e.clientY);

      // shared vertical to keep eyes level
      const nyShared = (L.ny + R.ny) / 2;

      // cap horizontal vergence (difference) so they never go bogey
      const avgX = (L.nx + R.nx) / 2;
      let diff = L.nx - R.nx;
      diff = clamp(diff, -MAX_VERG, MAX_VERG);

      const nxL = avgX + diff / 2;
      const nxR = avgX - diff / 2;

      gsap.to(l, {
        x: nxL * DX,
        y: nyShared * DY,
        duration: 0.45,
        ease: "power4.out",
        overwrite: true,
      });
      gsap.to(r, {
        x: nxR * DX,
        y: nyShared * DY,
        duration: 0.45,
        ease: "power4.out",
        overwrite: true,
      });
    }

    function resetEyes() {
      gsap.to([l, r], {
        x: 0,
        y: 0,
        duration: 0.35,
        ease: "power3.out",
        overwrite: true,
      });
    }

    window.addEventListener("mousemove", moveEyes);
    window.addEventListener("mouseleave", resetEyes);
    window.addEventListener("blur", resetEyes);

    return () => {
      window.removeEventListener("mousemove", moveEyes);
      window.removeEventListener("mouseleave", resetEyes);
      window.removeEventListener("blur", resetEyes);
    };
  }, []);

