  const root = useRef<HTMLDivElement | null>(null);

  // PUPIL ANIMATION

  useGSAP(() => {
    const leftPupil = document.getElementById("l-pupil");
    const rightPupil = document.getElementById("r-pupil");
    if (!leftPupil || !rightPupil) return;

    const prefersReduced =
      window.matchMedia?.("(prefers-reduced-motion: reduce)").matches ?? false;

    const isLargeScreen = window.matchMedia("(min-width: 1024px)").matches;
    if (!isLargeScreen || prefersReduced) return;

    const max = 12;

    const moveEyes = (x: number, y: number) => {
      const { innerWidth, innerHeight } = window;
      const xPercent = (x / innerWidth - 0.5) * 2;
      const yPercent = (y / innerHeight - 0.5) * 2;

      gsap.to(rightPupil, {
        x: xPercent * max, // normal
        y: yPercent * max,
        duration: 0.25,
        ease: "power2.out",
        transformOrigin: "50% 50%",
        transformBox: "fill-box",
      });

      gsap.to(leftPupil, {
        x: -xPercent * max, // ← flip X for mirrored left eye
        y: yPercent * max,
        duration: 0.25,
        ease: "power2.out",
        transformOrigin: "50% 50%",
        transformBox: "fill-box",
      });
    };

    const handleMouseMove = (e: MouseEvent) => {
      if (prefersReduced) return;
      moveEyes(e.clientX, e.clientY);
    };

    const resetEyes = () => {
      gsap.to([leftPupil, rightPupil], {
        x: 0,
        y: 0,
        duration: 0.35,
        ease: "power3.out",
      });
    };

    window.addEventListener("mousemove", handleMouseMove);
    window.addEventListener("mouseleave", resetEyes);
    window.addEventListener("blur", resetEyes);
    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
      window.removeEventListener("mouseleave", resetEyes);
      window.removeEventListener("blur", resetEyes);
    };
  }, []);

